import React, { useEffect, useState } from 'react'
import './App.css'
import SearchIcon from '@material-ui/icons/Search';
import Recipes from './components/Recipes'



const App= () => {

  const APP_ID = "db953df5";
  const APP_KEY = "46ead17b91ac0f940ea6a4ec77f964f9";


  const[recipes, setRecipes] = useState([]);


  useEffect(() => {
    getRecipes();

  },[])

  const getRecipes = async () => {
    const respons = await fetch(`https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${APP_KEY}`);
    const finelData = await respons.json();

    setRecipes(finelData.hits);
  }




  return (
    <div className="app">

      <form className="search-form">

        <input className="search-bar" type="text" placeholder="Search what you like foods...?"/>


        <button className="search-button" type="button" > <SearchIcon className="icon" /> </button>

      </form>

      {recipes.map(recipe => (
        <Recipes title={recipe.recipe.label} calories={recipe.recipe.calories} img={recipe.recipe.image} />
      ))}

    </div>
  )
}

export default App
